function startTimer(duration, display) {
    var time = duration, seconds;
    var timer = setInterval(function () {
        seconds = parseInt(duration);

        seconds = seconds < 10 ? "0" + seconds : seconds;

        display.html(time);

        if (--time < 0) {
            clearInterval(timer);
            $(".resend-btn").removeAttr("disabled");
        }
    }, 1000);
}

