                socialInfos.push(socialInfoL);
                var socialInfoT = {};
                socialInfoT.social = "Twitter";
                socialInfoT.url = twitterUrl[index].value ? twitterUrl[index].value : null;
                socialInfos.push(socialInfoT);
                member.socialInfos = socialInfos;
